
public class _03_char {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		char uniChar = '\u00A5'; //TODO  LOOKK UP INICODE NUMBER WORKS
		
		System.out.println(uniChar);
		
		char[] charArray = {'a', 'b', 'c', 'd', 'e'};
		
		System.out.println(charArray);
		
		
		String charArry = "abcde";
		System.out.println(charArry);
		
		//find a different unicode and assign to a variable
		//print it out
		//Create a new character array and print it out.
		
		char charArry2 = '\u0024';
		
		System.out.println(charArry2);

		
		
		System.out.println("Hello \"Tough Guy\", How are you?");
		
		System.out.println("James \nPaul");
				
		System.out.println("c:\\desktop\\user\\texthello.text");
		
		
		
//		\t  Inserts a tab in the text at this point.
//		\b  Inserts a backspace in the text at this point.
//		\n  Inserts a newline in the text at this point.
//		\r  Inserts a carriage return in the text at this point.
//		\f  Inserts a form feed in the text at this point.
//		\'  Inserts a single quote character in the text at this point.
//		\" Inserts a double quote character in the text at this point.
//		\\  Inserts a backslash character in the text at this point.
//		


		System.out.println(" ");
		
		System.out.println("t " +"James \tPaul");
		System.out.println("\\ " + "James \bPaul");
		System.out.println("n " + "James \nPaul");
		System.out.println("r " + "James \rPaul");
		System.out.println();
		System.out.println("f " + "James \fPaul");
		System.out.println(" ");
		System.out.println("' " + "James \'Paul");
		System.out.println("\"  " + "James \"Paul");
		System.out.println("\\ " + "James \\Paul");
		
		
		
		
		
	}

}
