package _09_loops;

public class _01_loops_while {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		
		int count = 0;
		while(count <= 10) 
		{
		//	System.out.println(count);
			count++;
		}
		
		System.out.println(count);
		
		System.out.println();
		
		
		// Write a count down loop
		
		int count2 = 10;
		while(count2 >= 0) 
		{
		//	System.out.println(count2);
			count2 --;
		}
		
		 System.out.println(count2);
		
		 System.out.println();
		 
		 
		// Write a count down from 100 by 5'sloop
			
		int count3 = 100;
		while(count3 >= 0) 
		{
		//	System.out.println(count3);
			count3 = count3 - 5;
		}
			
		// System.out.println(count3);	
	
		
		// Write a program 
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
